package day9;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(1+1);//2
		System.out.println(2-1);//1
		System.out.println(4/2);//2
		System.out.println(2*2);//4
		System.out.println(17%2);//give the remainder to you //1
		
		
		//increment & Decrement Operator
		
		int a=10;
	
		
		System.out.println(++a); // Pre Increment //Increment the current value of a by 1
		//Increment the value first , and then use the value 
	
		System.out.println(a++); //Post Increment //Increment the current value of a by 1
		//Use the current value and increment later
		
		System.out.println(a);//12
		
		
		int b =20;
		
		System.out.println(--b);//19
		System.out.println(b--);//19
		System.out.println(b);//18
		
		
		
	}

}
